import React from 'react';

const NewsItem = ({img, title, description, readMore, author}) => {
  return (
    <div>
        <img src={img} alt={img} />
        <h1>{title}</h1>
        <p>{description}</p>
        <h4>{author}</h4>
        <button>Read More</button>
    </div>
  );
};

export default NewsItem;
